import { Collapse } from 'react-collapse';
import { useState, useEffect } from 'react';
import { useWindowWidth } from '@react-hook/window-size'


export default function Menu({ title, items }) {

    const windowWidth = useWindowWidth();

    const [isOpen, setIsOpen] = useState(windowWidth > 768 ? true : false);

    const toggleCollapse = () => {
        if (windowWidth <= 768) {
            setIsOpen(!isOpen);
        }
    }

    return (
        <section>
            <nav className="grid gap-y-1 md:gap-y-4">
                <h6 onClick={toggleCollapse} className="text-lg text-primary-brand-color">{title}</h6>
                <Collapse isOpened={isOpen}>
                    <nav>
                        <ul className="grid gap-y-1 md:gap-y-2">
                            {items.map((item, key) => (
                                <li key={key}>
                                    <a href="#" className="text-sm hover:text-brand-color">
                                        {item.title}
                                    </a>
                                </li>
                            ))}
                        </ul>
                    </nav>
                </Collapse>

            </nav>
        </section>
    )
}
