import { useState, useEffect } from "react";
import Slider from "react-slick";
import Banners from 'api/banners.json';


export default function Campaigns() {

    const [banners, setBanners] = useEffect([]);

    useEffect(() => {
        setBanners(Banners);
    }, []);

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        arrows: false,
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        speed: 500,
        autoplaySpeed: 3500,
        cssEase: "linear"

    };

    return (
        <div className="container mx-auto">
            <Slider {...settings}>
                <div>
                    <img className="w-full object-cover" src="https://getir.com/_next/static/images/getir-mainpage-1-757eca6a46304def60cabce74d3f20a2.jpg" alt="" />
                </div>
                <div>
                    <img className="w-full object-cover" src="https://getir.com/_next/static/images/getir-mainpage-2-7c23764275cdaf14d7b6cf15ebbdd0c1.jpg" alt="" />
                </div>
                <div>
                    <img className="w-full object-cover" src="https://getir.com/_next/static/images/getir-mainpage-3-ffd1e98c5da51809f9adcc3a4a37d751.jpg" alt="" />
                </div>
            </Slider>
        </div>
    )
}
